const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/SpecDriver'))
const {Configuration} = require(cwd)
const {testSetup} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver
  let eyes
  let baselineTestName
  beforeEach(async () => {
    driver = await spec.build({device: "Samsung Galaxy S8", app: "https://applitools.bintray.com/Examples/eyes-android-hello-world.apk"} || {browser: 'chrome'})
    eyes = testSetup.getEyes({
      isVisualGrid: undefined,
      isCssStitching: true,
      branchName: "master",
    })
  })
  afterEach(async () => {
    await spec.cleanup(driver)
  })
  it('AppiumAndroidCheckRegion (@native-selectors @native @mobile)', async () => {
    await eyes.open(
            driver,
            "Applitools Eyes SDK",
            "AppiumAndroidCheckRegion",
            undefined,
          )
    await eyes.check({region: "android.widget.Button"})
    await eyes.close(true)
  })
})