const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/SpecDriver'))
const {Configuration} = require(cwd)
const {testSetup} = require('@applitools/sdk-shared')

describe.skip('Coverage Tests', () => {
  let driver
  let eyes
  let baselineTestName
  beforeEach(async () => {
    driver = await spec.build(undefined || {browser: 'chrome'})
    eyes = testSetup.getEyes({
      isVisualGrid: undefined,
      isCssStitching: true,
      branchName: "master",
    })
  })
  afterEach(async () => {
    await spec.cleanup(driver)
  })
  it('CheckRefreshableElement (@webdriver)', async () => {
    await spec.visit(driver, "http://localhost:5000/TestPages/RefreshDomPage/auto-refresh")
    await eyes.open(
            driver,
            "Applitools Eyes SDK",
            "CheckRefreshableElement",
            {width: 600, height: 500},
          )
    const var_3 = await spec.findElement(driver, {type: 'css', selector: "#inner-img"})
    await spec.click(driver, "#refresh-button")
    await eyes.check({region: var_3})
    await eyes.close(undefined)
  })
})