const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/SpecDriver'))
const {Configuration} = require(cwd)
const {testSetup} = require('@applitools/sdk-shared')

describe.skip('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  let baselineTestName
  beforeEach(async () => {
    [driver, destroyDriver] = await spec.build(undefined || {browser: 'chrome'})
    eyes = testSetup.getEyes({
      isVisualGrid: undefined,
      isCssStitching: true,
      branchName: "master",
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
  })
  it('CheckRefreshableElementInsideFrame (@webdriver)', async () => {
    await spec.visit(driver, "https://applitools.github.io/demo/TestPages/RefreshDomPage/iframe")
    await eyes.open(
            driver,
            "Applitools Eyes SDK",
            "CheckRefreshableElementInsideFrame",
            {width: 600, height: 500},
          )
    const frameElement = await spec.findElement(driver, {type: 'css', selector: "[name=\"frame\"]"})
    await spec.childContext(driver, frameElement)
    const var_5 = await spec.findElement(driver, {type: 'css', selector: "#inner-img"})
    await spec.click(driver, "#refresh-button")
    await spec.childContext(driver, null)
    await eyes.check({frames: [frameElement], region: var_5})
    await eyes.close(undefined)
  })
})