const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/SpecDriver'))
const {Configuration} = require(cwd)
const {testSetup} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver
  let eyes
  let baselineTestName
  beforeEach(async () => {
    driver = await spec.build(undefined || {browser: 'chrome'})
    eyes = testSetup.getEyes({
      isVisualGrid: undefined,
      isCssStitching: true,
      branchName: "master",
    })
  })
  afterEach(async () => {
    await spec.cleanup(driver)
  })
  it('CheckRegionInFrameLargerThenViewport', async () => {
    await spec.visit(driver, "https://applitools.github.io/demo/TestPages/OutOfViewport/")
    await eyes.open(
            driver,
            "Applitools Eyes SDK",
            "CheckRegionInFrameLargerThenViewport",
            {width: 800, height: 600},
          )
    await eyes.check({region: "#list", scrollRootElement: "body", frames: [{frame: "frame-list", scrollRootElement: "body"}], isFully: true})
    await eyes.close(undefined)
  })
})