const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/SpecDriver'))
const {Configuration} = require(cwd)
const {testSetup} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver
  let eyes
  let baselineTestName
  beforeEach(async () => {
    driver = await spec.build(undefined || {browser: 'chrome'})
    eyes = testSetup.getEyes({
      isVisualGrid: undefined,
      isCssStitching: true,
      branchName: "master",
    })
  })
  afterEach(async () => {
    await spec.cleanup(driver)
  })
  it('CheckStaleElement (@webdriver)', async () => {
    await spec.visit(driver, "https://applitools.github.io/demo/TestPages/RefreshDomPage")
    await eyes.open(
            driver,
            "Applitools Eyes SDK",
            "CheckStaleElement",
            {width: 600, height: 500},
          )
    const var_3 = await spec.findElement(driver, {type: 'css', selector: "#inner-img"})
    await spec.click(driver, "#invalidate-button")
    await assert.rejects(
          async () => {
    const var_6 = await eyes.check({region: var_3})
    return var_6
    },
          error => {
    const var_9 = spec.isStaleElementError(error)
    return var_9
    },
          undefined,
        )
    await eyes.close(false)
  })
})