const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/SpecDriver'))
const {Configuration} = require(cwd)
const {testSetup} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  let baselineTestName
  beforeEach(async () => {
    [driver, destroyDriver] = await spec.build(undefined || {browser: 'chrome'})
    eyes = testSetup.getEyes({
      isVisualGrid: true,
      isCssStitching: undefined,
      branchName: "master",
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
  })
  it('CheckWindowOnJsLayoutPage_VG', async () => {
    await spec.visit(driver, "https://applitools.github.io/demo/TestPages/JsLayout")
    await eyes.setConfiguration(new Configuration({browsersInfo: [{name: "chrome", width: 1000, height: 800}, {iosDeviceInfo: {deviceName: "iPad (7th generation)"}}, {chromeEmulationInfo: {deviceName: "Pixel 4 XL"}}]}))
    await eyes.open(
            driver,
            "Applitools Eyes SDK",
            "CheckWindowOnJsLayoutPage_VG",
            undefined,
          )
    await eyes.check({layoutBreakpoints: [500, 1000]})
    await eyes.close(undefined)
  })
})