const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/SpecDriver'))
const {Configuration} = require(cwd)
const {testSetup} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  let baselineTestName
  beforeEach(async () => {
    [driver, destroyDriver] = await spec.build(undefined || {browser: 'chrome'})
    eyes = testSetup.getEyes({
      isVisualGrid: undefined,
      isCssStitching: undefined,
      branchName: "master",
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
  })
  it('TestAcmeLogin_Scroll', async () => {
    await spec.visit(driver, "https://afternoon-savannah-68940.herokuapp.com/#")
    await eyes.open(
            driver,
            "Eyes Selenium SDK - ACME",
            "TestAcmeLogin_Scroll",
            {width: 1024, height: 768},
          )
    const username = await spec.findElement(driver, {type: 'css', selector: "#username"})
    await spec.type(driver, username, "adamC")
    const password = await spec.findElement(driver, {type: 'css', selector: "#password"})
    await spec.type(driver, password, "MySecret123?")
    await eyes.check({region: "#username"})
    await eyes.check({region: "#password"})
    await eyes.close(true)
  })
})