const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/SpecDriver'))
const {Configuration} = require(cwd)
const {testSetup} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver
  let eyes
  let baselineTestName
  beforeEach(async () => {
    driver = await spec.build(undefined || {browser: 'chrome'})
    eyes = testSetup.getEyes({
      isVisualGrid: true,
      isCssStitching: undefined,
      branchName: "master",
    })
  })
  afterEach(async () => {
    await spec.cleanup(driver)
  })
  it('TestCheckRegionInAVeryBigFrameAfterManualSwitchToFrame_VG (@webdriver)', async () => {
    await spec.visit(driver, "https://applitools.github.io/demo/TestPages/WixLikeTestPage/index.html")
    await eyes.open(
            driver,
            "Eyes Selenium SDK - Special Cases",
            "TestCheckRegionInAVeryBigFrameAfterManualSwitchToFrame_VG",
            {width: 700, height: 460},
          )
    const var_3 = await spec.findElement(driver, {type: 'css', selector: "[name=\"frame1\"]"})
    await spec.childContext(driver, var_3)
    await eyes.check({region: "img"})
    await eyes.close(true)
  })
})