const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/SpecDriver'))
const {Configuration} = require(cwd)
const {testSetup} = require('@applitools/sdk-shared')

describe.skip('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  let baselineTestName
  beforeEach(async () => {
    [driver, destroyDriver] = await spec.build(undefined || {browser: 'chrome'})
    eyes = testSetup.getEyes({
      isVisualGrid: undefined,
      isCssStitching: true,
      branchName: "master",
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
  })
  it('TestCheckRegionInFrame3_Fluent', async () => {
    await spec.visit(driver, "https://applitools.github.io/demo/TestPages/FramesTestPage/")
    await eyes.open(
            driver,
            "Eyes Selenium SDK - Fluent API",
            "TestCheckRegionInFrame3_Fluent",
            {width: 700, height: 460},
          )
    await eyes.check({frames: ["[name=\"frame1\"]"], floatingRegions: [{region: {left: 25, top: 25, width: 25, height: 25}, maxUpOffset: 200, maxDownOffset: 200, maxLeftOffset: 150, maxRightOffset: 150}], matchLevel: "Layout", isFully: true})
    await eyes.close(true)
  })
})