const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/SpecDriver'))
const {Configuration} = require(cwd)
const {testSetup} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  let baselineTestName
  beforeEach(async () => {
    [driver, destroyDriver] = await spec.build({browser: "firefox"} || {browser: 'chrome'})
    eyes = testSetup.getEyes({
      isVisualGrid: undefined,
      isCssStitching: undefined,
      branchName: "master",
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
  })
  it('TestHorizonalScroll_Scroll (@firefox)', async () => {
    await spec.visit(driver, "https://applitools.github.io/demo/TestPages/horizontal-scroll.html")
    await eyes.open(
            driver,
            "Applitools Eyes SDK",
            "TestHorizonalScroll_Scroll",
            {width: 600, height: 400},
          )
    await eyes.check({isFully: true})
    await eyes.close(true)
  })
})