const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/SpecDriver'))
const {Configuration} = require(cwd)
const {testSetup} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  let baselineTestName
  beforeEach(async () => {
    [driver, destroyDriver] = await spec.build({browser: "edge-18"} || {browser: 'chrome'})
    eyes = testSetup.getEyes({
      isVisualGrid: undefined,
      isCssStitching: true,
      branchName: "master",
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
  })
  it('TestSetViewportSizeEdge (@edge)', async () => {
    await eyes.constructor.setViewportSize(driver, {width: 600, height: 600})
    const actualViewportSize = await spec.executeScript(driver, "return {width: window.innerWidth, height: window.innerHeight}", ...[])
    assert.deepStrictEqual(actualViewportSize, {width: 600, height: 600}, undefined)
  })
})