const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/SpecDriver'))
const {Configuration} = require(cwd)
const {testSetup} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver
  let eyes
  let baselineTestName
  beforeEach(async () => {
    driver = await spec.build(undefined || {browser: 'chrome'})
    eyes = testSetup.getEyes({
      isVisualGrid: true,
      isCssStitching: undefined,
      branchName: "master",
    })
  })
  afterEach(async () => {
    await spec.cleanup(driver)
  })
  it('TestVisualLocators_VG', async () => {
    await spec.visit(driver, "https://applitools.github.io/demo/TestPages/FramesTestPage/")
    await eyes.open(
            driver,
            "Applitools Eyes SDK",
            "TestVisualLocators_VG",
            undefined,
          )
    const regionsMap = await eyes.locate({locatorNames: ["applitools_title"]})
    await eyes.close(false)
    assert.deepStrictEqual(regionsMap, {applitools_title: [{left: 2, top: 11, width: 173, height: 58}]}, undefined)
  })
})